import React from 'react';
import "./Home.css";
import edu from '../../assets/edu.svg';
import timer from '../../assets/timer.svg';
import live from '../../assets/live.svg';
import degree from '../../assets/degree.svg';
import ads from '../../assets/ads.svg';
import Card from '../card/Card.jsx';

const Lists = (props) => {
  const tt = (<div><p>{props.txt} <span className='high'>{props.highlight}</span></p></div>);
  const tt2 = (<div><p><span className='high'>{props.highlight}</span> {props.txt}</p></div>);

  return (
    <div className='flexy'>
      <img className='images' alt="" src={props.imgname}></img>
      <div>{props.booly === "true" ? tt2 : tt}</div>
    </div>
  );
};

const Home = () => {
  return (
    <div className='home'>
      <div className="home-left-parent" >
        <div className="heading">
          <h1>Access curated courses worth <br />
            <span style={{ fontSize: '40px' }}> ₹&nbsp;
              <span
                style={{ textDecoration: 'line-through', textDecorationColor: 'red', fontFamily: 'Roboto' }}
              >
                18,500
              </span>
            </span>
            &nbsp;at just
            <span style={{ fontSize: '40px', color: '#0096FF', fontFamily: 'Roboto' }}> ₹99 </span>
            per year!
          </h1>
          <div>
            <div style={{ paddingTop: '20px' }}>
              <Lists imgname={edu} highlight="100+" txt="Job relevant cources" booly="true" />
              <Lists imgname={timer} highlight="20000+" txt="Hours of content" booly="true" />
              <Lists imgname={live} highlight="Exclusive" txt="Webinar access" booly="true" />
              <Lists imgname={degree} txt="Scolarship worth" highlight="₹95,400" booly="false" />
              <Lists imgname={ads} highlight="Ad Free" txt="learning experience" booly="true" />
            </div>
          </div>
        </div>
      </div>

      <div className="home-right-parent">
        <Card />
      </div>
    </div>
  );
};

export default Home;
