import React, { useState } from 'react';
import SubsPlan from '../subsplan/SubsPlan.jsx';
import './Card.css';
import time from '../../assets/e-time.svg';
import razorpay from '../../assets/razorpay.svg';

const Card = () => {
  const [active, setActive] = useState('p2');
  const [totalFee, setTotalFee] = useState(18500);
  const [tot, setTot] = useState('18500');

  const handlePlanClick = (id, tPrice) => {
    setActive(id);
    setTot(tPrice);
    const discount = id === 'p1' ? 9900 : 0; // Adjust the discount for 'p1' plan
    setTotalFee(18500 - (Number(tPrice.substring(1)) - discount));
  };

  return (
    <div className="right-box">
      <div className="entry-step">
        {/* ... */}
      </div>

      <div className="right-box-head">
        <h3>Select your subscription plan</h3>
      </div>

      <div className="subs-plan">
        <SubsPlan
          id='p1'
          heading='12 Months Subscription'
          mPrice='₹8'
          tPrice='₹99'
          rec='false'
          active={active}
          setActive={handlePlanClick}
          tot={tot}
          setTot={setTot}
          expired='true'
        />
        <SubsPlan
          id='p2'
          heading='12 Months Subscription'
          mPrice='₹15'
          tPrice='₹179'
          rec='true'
          active={active}
          setActive={handlePlanClick}
          tot={tot}
          setTot={setTot}
          expired='false'
        />
        <SubsPlan
          id='p3'
          heading='6 Months Subscription'
          mPrice='₹25'
          tPrice='₹149'
          rec='false'
          active={active}
          setActive={handlePlanClick}
          tot={tot}
          setTot={setTot}
          expired='false'
        />
        <SubsPlan
          id='p4'
          heading='3 Months Subscription'
          mPrice='₹33'
          tPrice='₹99'
          rec='false'
          active={active}
          setActive={handlePlanClick}
          tot={tot}
          setTot={setTot}
          expired='false'
        />
      </div>

      <div className='total-div'>
        {/* ... */}
        <div className="total-fee-container">
          <p className='total-head'><strong>Total </strong>(Incl. of 18% GST)</p>
          <p className="total-fee">{tot}</p>
        </div>
      </div>

      <div className='button'>
        {/* ... */}
      </div>
      <div>
        <img alt="" src={razorpay}></img>
      </div>
    </div>
  );
};

export default Card;
