import React, { useState, useEffect } from 'react';
import SubsPlan from '../subsplan/SubsPlan.jsx';
import './Card.css';
import './SubsPlan.css';
import time from '../../assets/e-time.svg';
import razorpay from '../../assets/razorpay.svg';

const Card = () => {
  const [active, setActive] = useState('p2');
  const [totalFee, setTotalFee] = useState(18500);
  const [tot, setTot] = useState('18500');
  const gst = 0.18;

  useEffect(() => {
    const subtotal = Number(tot.substring(1));
    const gstAmount = subtotal * gst;
    const totalPrice = subtotal + gstAmount;
    setTotalFee(subtotal);
  }, [tot]);

  const handlePlanClick = (id, tPrice) => {
    setActive(id);
    setTot(tPrice);
  };

  return (
    <div className="right-box">
      <div className="entry-step">
        <div className="entry-step1">
          <button>1</button>
          <p>Sign Up</p>
        </div>
        <div className="entry-step2">
          <button>2</button>
          <p>Subscribe</p>
        </div>
      </div>

      <div className="right-box-head">
        <h3>Select your subscription plan</h3>
      </div>

      <div className="subs-plan">
        <SubsPlan
          id="p1"
          heading="12 Months Subscription"
          mPrice="₹8"
          tPrice="₹99"
          rec={false}
          active={active}
          onClick={handlePlanClick}
          expired={true}
        />
        <SubsPlan id='p2'
          heading='12 Months Subscription' mPrice='₹15' tPrice='₹179' rec='true' active={active} setActive={setActive} tot={tot} setTot={setTot} expired='false' />
        <SubsPlan id='p3' heading='6 Months Subscription' mPrice='₹25' tPrice='₹149' rec='false' active={active} setActive={setActive} tot={tot} setTot={setTot} expired='false' />
        <SubsPlan id='p4' heading='3 Months Subscription' mPrice='₹33' tPrice='₹99' rec='false' active={active} setActive={setActive} tot={tot} setTot={setTot} expired='false' />
      </div>

      <div className='total-div'>

        <div className='total-div'>
          <div className="sub-fee-container">
            <p className='sub-head'>Subscription Fee</p>
            <p className="sub-fee">₹{totalFee}</p>
          </div>
          <div className="ltd-time-container">
            <div className="ltd-title">
              <p className="ltd-head">Limited time offer</p>
              <div className="ltd-subhead">
                <img src={time} alt="ltd-time" stroke='#000' />
                <p className="ltd-subhead-txt"><span></span> Offer valid till 25th March 2023 </p>
              </div>
            </div>

            <div className='ltd-price'>
              <p className='ltd-price-p'>-₹ {totalFee - Number(tot.substring(1))}</p>
            </div>
          </div>
          <div className="total-fee-container">
            <p className='total-head'><strong>Total </strong>(Incl. of 18% GST)</p>
            <p className="total-fee">{totalPrice.toFixed(2)}</p>
          </div>
        </div>
      </div>

      <div className='button'>
        <button className='cancel-button'>Cancel</button>
        <button className='proceed-button'>Proceed to Pay</button>

      </div>
      <div className='image'>
        <img alt="" src={razorpay}></img>
      </div>
    </div>
  );
};

const SubPlan = (props) => {
  const { id, heading, mPrice, tPrice, rec, expired, active, onClick } = props;

  const handleClick = () => {
    if (id !== 'p1') {
      onClick(id, tPrice);
    }
  };

  const isRecommended = rec || expired;
  const statusText = expired ? 'Offer Expired' : 'Recommended';

  return (
    <div onClick={handleClick}>
      <div
        className="subs-container"
        style={{
          borderColor: expired
            ? 'var(--text3)'
            : active === id
              ? 'var(--success)'
              : 'var(--text3)',
          background: expired
            ? '#E7E7E7'
            : active === id
              ? '#D7EDDD'
              : 'var(--text4)',
        }}
      >
        {isRecommended && (
          <div className={expired ? 'expired' : 'recommended'}>
            <p>{statusText}</p>
          </div>
        )}
        <div className="subs-left-container">
          <div className="bullet" style={expired === 'true' ? { background: "var(--text3)", outlineColor: "var(--text3)" } : selected === 'true' ? { background: "var(--success)", outlineColor: "var(--success)" } : { background: "var(--text4)", outlineColor: "var(--text3)" }}>

          </div>
          <div className="months" style={expired === 'true' ? { color: "#BEBEBE" } : {}}>
            {props.heading}
          </div>
        </div>
        <div className="subs-right-container" >
          <div className="total-price">
            <p className="total" style={expired === 'true' ? { color: "#BEBEBE" } : {}}>Total</p>
            <p className="price" style={expired === 'true' ? { color: "#BEBEBE" } : {}}>{props.tPrice}</p>
          </div>
          <div className="per-month">
            <p className="m-price" style={expired === 'true' ? { color: "#BEBEBE" } : {}}>{props.mPrice}</p>
            <p className="month">/mo</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Card;
