import React, { useState } from 'react';
import SubPlan from './SubPlan'; // Assuming SubPlan.js is in the same directory

const PaymentsPage = () => {
  const [activePlan, setActivePlan] = useState(null);
  const [totalPrice, setTotalPrice] = useState(0);

  const plans = [
    {
      id: 'p1',
      heading: 'Basic Plan',
      tPrice: 100,
      mPrice: 10,
      expired: 'false',
      rec: 'false',
    },
    // Add more plans here
  ];

  const handlePlanClick = (id, tPrice) => {
    setActivePlan(id);
    setTotalPrice(tPrice);
  };

  return (
    <div>
      {plans.map((plan) => (
        <SubPlan
          key={plan.id}
          id={plan.id}
          heading={plan.heading}
          tPrice={plan.tPrice}
          mPrice={plan.mPrice}
          expired={plan.expired}
          rec={plan.rec}
          active={activePlan}
          setActive={handlePlanClick}
        />
      ))}
      <div>
        <p>Total Price: {totalPrice}</p>
      </div>
    </div>
  );
};

export default PaymentsPage;
