import { useState, useEffect } from 'react';
import './SubsPlan.css';

const SubPlan = (props) => {
  const [selected, setSelected] = useState(false);
  const expired = props.expired;

  useEffect(() => {
    const handlePinLoad = () => {
      if (props.active === props.id) {
        setSelected(true);
        props.setTot(props.tPrice);
      } else {
        setSelected(false);
      }
    };
    handlePinLoad();
  }, [props.active, props]);

  const handleContainerClick = () => {
    if (props.id !== 'p1') {
      props.setActive(props.id);
      setSelected(!selected); // Toggle the checked state
    }
  };

  const tt = (
    <div className={expired === 'true' ? 'expired' : 'recommended'}>
      <p>{expired === 'true' ? 'Offer Expired' : 'Recommended'}</p>
    </div>
  );

  return (
    <div onClick={handleContainerClick}>
      <div
        className="subs-container"
        style={
          expired === 'true'
            ? { borderColor: 'var(--text3)', background: '#E7E7E7' }
            : selected
              ? { borderColor: 'var(--success)', background: '#D7EDDD' }
              : { borderColor: 'var(--text3)', background: 'var(--text4)' }
        }
      >
        {props.rec === 'true' || props.expired === 'true' ? tt : ''}
        <div
          className="subs-left-container"
          style={expired === 'true' ? { color: '#BEBEBE' } : {}}
        >
          <div
            className="bullet"
            style={
              expired === 'true'
                ? { background: 'var(--text3)', outlineColor: 'var(--text3)' }
                : selected
                  ? { background: 'var(--success)', outlineColor: 'var(--success)' }
                  : { background: 'var(--text4)', outlineColor: 'var(--text3)' }
            }
          >
            {selected && <span className="check-mark">âœ“</span>}
          </div>
          <div className="months">{props.heading}</div>
        </div>
        <div className="subs-right-container" >
          <div className="total-price">
            <p className="total" style={expired === 'true' ? { color: "#BEBEBE" } : {}}>Total</p>
            <p className="price" style={expired === 'true' ? { color: "#BEBEBE" } : {}}>{props.tPrice}</p>
          </div>
          <div className="per-month">
            <p className="m-price" style={expired === 'true' ? { color: "#BEBEBE" } : {}}>{props.mPrice}</p>
            <p className="month">/mo</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SubPlan;
