import React from 'react';
import "./Home.css";
import a from '../../assets/a.svg';
import b from '../../assets/b.svg';
import live from '../../assets/live.svg';
import degree from '../../assets/degree.svg';
import ads from '../../assets/ads.svg';
import Card from '../card/Card.jsx';

const withItemsRendering = (Component) => {
  const items = [
    { imgname: a, highlight: "100+", txt: "Job relevant cources", booly: true },
    { imgname: b, highlight: "20000+", txt: "Hours of content", booly: true },
    { imgname: live, highlight: "Exclusive", txt: "Webinar access", booly: true },
    { imgname: degree, txt: "Scolarship worth", highlight: "₹95,400", booly: false },
    { imgname: ads, highlight: "Ad Free", txt: "learning experience", booly: true }
  ];

  return () => (
    <Component items={items} />
  );
};

const Home = ({ items }) => {
  return (
    <div className='home'>
      <div className="home-left-parent" >
        <div className="heading">
          <h1>Access curated courses worth <br />
            <span style={{ fontSize: '40px' }}> ₹&nbsp;
              <span
                style={{ textDecoration: 'line-through', textDecorationColor: 'red', fontFamily: 'Roboto' }}
              >
                18,500
              </span>
            </span>
            &nbsp;at just
            <span style={{ fontSize: '40px', color: '#0096FF', fontFamily: 'Roboto' }}> ₹99 </span>
            per year!
          </h1>
          <div>
            <div style={{ paddingTop: '20px' }}>
              {items.map((item, index) => (
                <div className='flexy' key={index}>
                  <img className='images' alt="" src={item.imgname}></img>
                  <div>
                    {item.booly ? (
                      <p>{item.txt} <span className='high'>{item.highlight}</span></p>
                    ) : (
                      <p><span className='high'>{item.highlight}</span> {item.txt}</p>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      <div className="home-right-parent">
        <Card />
      </div>
    </div>
  );
};

const HomeWithItems = withItemsRendering(Home);

export default HomeWithItems;
